// Persistent deduplication: track published news for 5 days
const staticData = $getWorkflowStaticData('global');

// Initialize history if not exists
if (!staticData.publishedNews) {
  staticData.publishedNews = [];
}

const fiveDaysAgo = new Date();
fiveDaysAgo.setDate(fiveDaysAgo.getDate() - 5);

// Clean old entries (older than 5 days)
staticData.publishedNews = staticData.publishedNews.filter(
  h => new Date(h.date) > fiveDaysAgo
);

const filtered = $input.all().filter(item => {
  const newsTitle = (item.json.title || '').trim().toLowerCase();
  const newsDate = new Date(item.json.pubDate || item.json.isoDate || new Date());

  // Skip news older than 12 hours
  const twelveHoursAgo = new Date();
  twelveHoursAgo.setHours(twelveHoursAgo.getHours() - 12);
  if (newsDate < twelveHoursAgo) {
    return false;
  }

  // Check if already published in last 5 days
  const isDuplicate = staticData.publishedNews.some(h => {
    const hTitle = (h.title || '').trim().toLowerCase();
    // Exact match or very similar (first 50 chars match)
    return hTitle === newsTitle || 
           (newsTitle.length > 50 && hTitle.substring(0, 50) === newsTitle.substring(0, 50));
  });

  if (isDuplicate) {
    return false;
  }

  // Add to history
  staticData.publishedNews.push({
    title: newsTitle,
    date: new Date().toISOString()
  });

  return true;
});

return filtered;
